-- Create "password_reset_token" table
CREATE TABLE "password_reset_token" ("id" serial NOT NULL, "user_id" integer NOT NULL, "token" character varying(255) NOT NULL, "user_ip" character varying(255) NOT NULL, "expired" boolean NOT NULL DEFAULT false, "expires_at" timestamptz NOT NULL, "created_at" timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, "updated_at" timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, "deleted_at" timestamptz NULL, PRIMARY KEY ("id"), CONSTRAINT "password_reset_token_token_key" UNIQUE ("token"), CONSTRAINT "password_reset_token_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "app_user" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
